import React, {Component} from 'react';
import Urbit from '@urbit/http-api';


class App extends Component {
  urbit = new Urbit("http://localhost:8080", "lidlut-tabwed-pillex-ridrup");
  state = {
    entries: [],
  };

  constructor() {
    super();
    this.urbit.ship = "zod"
  }

  componentDidMount() {
    this.getEntries();
    this.subscribe();
  }

  getEntries = async () => {
    const {entries: e} = this.state;
    const before = (e.length === 0) ? Date.now() : e[e.length - 1].id;
    const max = 2;
    const path = `/entries/before/${before}/${max}`;
    const result = await this.urbit.scry({
      app: "journal",
      path: path
    });
    this.handleUpdate(result);
  };

  subscribe = () => {
    this.urbit.subscribe({
      app: "journal",
      path: "/updates",
      event: this.handleUpdate,
    })
  };

  spot = (id) => {
    const {entries: e} = this.state;
    let low = 0
    let high = e.length;
    while (low < high) {
      let mid = (low + high) >>> 1;
      if (e[mid].id > id) low = mid + 1;
      else high = mid;
    }
    return low;
  }

  handleUpdate = (upd) => {
    const items = this.state.entries;
    if ("entries" in upd) {
      this.setState({entries: items.concat(upd.entries)})
    } else if ("add" in upd) {
      const ind = this.spot(upd.add.id);
      if (upd.add.id > items[items.length - 1].id) items.splice(ind, 0, upd.add);
      this.setState({entries: items});
    } else if ("edit" in upd) {
      const ind = items.findIndex(e => e.id === upd.edit.id);
      if (ind !== -1) items[ind] = upd.edit;
      this.setState({entries: items});
    } else if ("del" in upd) {
      const ind = items.findIndex(e => e.id === upd.del.id);
      if (ind !== -1) items.splice(ind, 1);
      this.setState({entries: items});
    };
  };

  printEntry = ({id, txt}) => {
    const reg = /(?:\r?\n[ \t]*){2,}(?!\s*$)/;
    return (
      <li key={id}>
        {txt.split(reg).map((e, i) => <p key={i}>{e}</p>)}
      </li>
    )
  };

  render() {
    return (
      <React.Fragment>
        <button onClick={()=>this.getEntries()}>Moar</button>
        <ul>{this.state.entries.map(e => this.printEntry(e))}</ul>
      </React.Fragment>
    );
  }
}

export default App;
