import React, {Component} from 'react';
import Urbit from '@urbit/http-api';
import 'bootstrap/dist/css/bootstrap.min.css';
import TextareaAutosize from 'react-textarea-autosize';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import Stack from 'react-bootstrap/Stack';

class App extends Component {
  urbit = new Urbit("http://localhost:8080", "lidlut-tabwed-pillex-ridrup");
  state = {
    entries: [],
  };

  constructor() {
    super();
    this.urbit.ship = "zod"
  }

  componentDidMount() {
    this.getEntries();
    this.subscribe();
  }

  getEntries = async () => {
    const {entries: e} = this.state;
    const before = (e.length === 0) ? Date.now() : e[e.length - 1].id;
    const max = 2;
    const path = `/entries/before/${before}/${max}`;
    const result = await this.urbit.scry({
      app: "journal",
      path: path
    });
    this.handleUpdate(result);
  };

  subscribe = () => {
    this.urbit.subscribe({
      app: "journal",
      path: "/updates",
      event: this.handleUpdate,
    })
  };

  delete = (id) => {
    this.urbit.poke({
      app: "journal",
      mark: "journal-action",
      json: {"del": {"id": id}}
    })
  };

  submitEdit = (id, txt) => {
    this.urbit.poke({
      app: "journal",
      mark: "journal-action",
      json: {"edit": {"id": id, "txt": txt}}
    })
  };

  spot = (id) => {
    const {entries: e} = this.state;
    let low = 0
    let high = e.length;
    while (low < high) {
      let mid = (low + high) >>> 1;
      if (e[mid].id > id) low = mid + 1;
      else high = mid;
    }
    return low;
  }

  handleUpdate = (upd) => {
    const items = this.state.entries;
    if ("entries" in upd) {
      const newItems = items.concat(
        upd.entries.map(e => ({...e, edit: false, draft: null}))
      );
      this.setState({entries: newItems})
    } else if ("add" in upd) {
      const ind = this.spot(upd.add.id);
      if (upd.add.id > items[items.length - 1].id) {
        items.splice(ind, 0, {...upd.add, edit: false, draft: null})
      };
      this.setState({entries: items});
    } else if ("edit" in upd) {
      const ind = items.findIndex(e => e.id === upd.edit.id);
      if (ind !== -1) {
        items[ind] = {...upd.edit, edit: false, draft: null}
      };
      this.setState({entries: items});
    } else if ("del" in upd) {
      const ind = items.findIndex(e => e.id === upd.del.id);
      if (ind !== -1) items.splice(ind, 1);
      this.setState({entries: items});
    };
  };

  cancelEdit = (id) => {
    const {entries} = this.state;
    const ind = entries.findIndex(e => e.id === id);
    if (ind !== -1) {
      entries[ind] = {...entries[ind], edit: false, draft: null};
      this.setState({entries: entries})
    }
  };

  saveDraft = (id, txt) => {
    const {entries} = this.state;
    const ind = entries.findIndex(e => e.id === id);
    if (ind !== -1) {
      entries[ind] = {...entries[ind], draft: txt};
      this.setState({entries: entries})
    }
  };

  setEdit = (id) => {
    const {entries} = this.state;
    const ind = entries.findIndex(e => e.id === id);
    if (ind !== -1) {
      entries[ind] = {...entries[ind], edit: true};
      this.setState({entries: entries})
    }
  };

  editBox = (id, txt, draft) => {
    return (
      <TextareaAutosize
        className="w-100"
        value={(draft === null) ? txt : draft}
        onChange={(e) => this.saveDraft(id, e.target.value)}
      />
    );
  };

  submitButton = (id, draft) => {
    return (
      <Button
        variant="outline-primary"
        onClick={()=>this.submitEdit(id, draft)}
      />
    );
  };

  cancelButton = (id) => {
    return (
      <Button variant="outline-danger" onClick={()=>this.cancelEdit(id)}>
        Cancel
      </Button>
    )
  };

  editButton = (id) => {
    return (
      <Button variant="outline-primary" onClick={()=>this.setEdit(id)}>
        Edit
      </Button>
    )
  };

  deleteButton = (id) => {
    return (
      <Button
        variant="outline-danger"
        onClick={()=>this.delete(id)}
      />
    )
  };

  printEntry = ({id, txt, edit, draft}) => {
    const reg = /(?:\r?\n[ \t]*){2,}(?!\s*$)/;
    let d = new Date(id);
    return (
      <Card key={id.toString()} style={{maxWidth: "40rem", width: "100%"}}>
        <Card.Header className="fs-4">
          {d.toLocaleString()}
        </Card.Header>
        <Card.Body>
          {edit
           ? ()=>this.editBox(id, txt, draft)
           : txt.split(reg).map((e, ind) => <p key={ind.toString()}>{e}</p>)
          }
        </Card.Body>
        <Card.Footer className="d-flex flex-wrap justify-content-between">
          {(edit) ? this.submitButton(id, draft) : this.editButton(id)}
          {(edit) ? this.cancelButton(id): this.deleteButton(id)}
        </Card.Footer>
      </Card>
    )
  };

  render() {
    return (
      <React.Fragment>
        <Button onClick={()=>this.getEntries()}>Moar</Button>
        <Stack gap={5} className="m-3 d-flex align-items-center">
          {this.state.entries.map(e => this.printEntry(e))}
        </Stack>
      </React.Fragment>
    );
  }
}

export default App;
