import React, {Component} from 'react';
import Urbit from '@urbit/http-api';

class App extends Component {
  urbit = new Urbit("http://localhost:8080", "lidlut-tabwed-pillex-ridrup");
  state = {
    entries: [],
    newest: null,
    oldest: null
  };

  constructor() {
    super();
    this.urbit.ship = "zod";
  };

  componentDidMount() {
    this.getEntries(Date.now(), 5);
  };

  async getEntries(time, count) {
    const path = `/entries/before/${time}/${count}`;
    const result = await this.urbit.scry({
      app: "journal",
      path: path
    });
    if ("entries" in result) {
      const entries = result.entries;
      const newest = (entries.length > 0)
            ? entries[0].id : this.state.newest;
      const oldest = (entries.length > 0)
            ? entries[entries.length - 1].id
            : this.state.oldest;
      this.setState({
        entries: this.state.entries.concat(entries),
        newest: newest,
        oldest: oldest
      });
    };
  };


  render() {
    return <pre>{JSON.stringify(this.state, null, 2)}</pre>;
  }
}

export default App;
