import React, {Component} from 'react';
import Urbit from '@urbit/http-api';

class App extends Component {
  urbit = new Urbit("http://localhost:8080", "lidlut-tabwed-pillex-ridrup");
  state = {
    entries: [],
    newest: null,
    oldest: null
  };

  constructor() {
    super();
    this.urbit.ship = "zod";
    this.getEntries = this.getEntries.bind(this);
  };

  componentDidMount() {
    this.setState(this.getEntries());
  };

  async getEntries() {
    const before = (this.state.oldest == null)
          ? Date.now() : this.state.oldest;
    const max = 1;
    const path = `/entries/before/${before}/${max}`;
    const result = await this.urbit.scry({
      app: "journal",
      path: path
    });
    const entries = result.entries;
    const newest = (entries.length > 0)
          ? entries[0].id : this.state.newest;
    const oldest = (entries.length > 0)
          ? entries[entries.length - 1].id
          : this.state.oldest;
    this.setState({
      entries: this.state.entries.concat(entries),
      newest: newest,
      oldest: oldest
    });
  };

  render() {
    return (
      <React.Fragment>
        <button onClick={this.getEntries()}>Moar</button>
        <pre>{JSON.stringify(this.state, null, 2)}</pre>
      </React.Fragment>
    );
  }
}

export default App;
