import React, {Component} from 'react';
import Urbit from '@urbit/http-api';


window.urbit = new Urbit("http://localhost:8080", "lidlut-tabwed-pillex-ridrup");
window.urbit.ship = "zod";

class App extends Component {
  state = {
    entries: [],
  };

  componentDidMount() {
    this.getEntries();
    this.subscribe();
  }

  getEntries = async () => {
    const {entries: e} = this.state;
    const before = (e.length === 0) ? Date.now() : e[e.length - 1].id;
    const max = 6;
    const path = `/entries/before/${before}/${max}`;
    const result = await window.urbit.scry({
      app: "journal",
      path: path
    });
    this.handleUpdate(result);
  };

  subscribe = () => {
    window.urbit.subscribe({
      app: "journal",
      path: "/updates",
      event: this.handleUpdate,
      quit: () => console.log("quit"),
      err: () => console.log("error")
    })
  };

  spot(id) {
    const {entries: e} = this.state;
    let low = 0
    let high = e.length;
    while (low < high) {
      let mid = (low + high) >>> 1;
      if (e[mid].id > id) low = mid + 1;
      else high = mid;
    }
    return low;
  }

  handleUpdate = (upd) => {
    const items = this.state.entries;
    if ("entries" in upd) {
      this.setState({entries: items.concat(upd.entries)})
    } else if ("add" in upd) {
      const ind = this.spot(upd.add.id);
      if (upd.add.id > items[items.length - 1].id) items.splice(ind, 0, upd.add);
      this.setState({entries: items});
    } else if ("edit" in upd) {
      const ind = items.findIndex(e => e.id === upd.edit.id);
      if (ind !== -1) items[ind] = upd.edit;
      this.setState({entries: items});
    } else if ("del" in upd) {
      const ind = items.findIndex(e => e.id === upd.del.id);
      if (ind !== -1) items.splice(ind, 1);
      this.setState({entries: items});
    };
  };

  render() {
    return (
      <React.Fragment>
        <button onClick={()=>this.getEntries()}>Moar</button>
        <pre>{JSON.stringify(this.state, null, 2)}</pre>
      </React.Fragment>
    );
  }
}

export default App;
