import React, {Component} from 'react';
import Urbit from '@urbit/http-api';
import 'bootstrap/dist/css/bootstrap.min.css';
import TextareaAutosize from 'react-textarea-autosize';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import Stack from 'react-bootstrap/Stack';
import Tab from 'react-bootstrap/Tab';
import Tabs from 'react-bootstrap/Tabs';

class App extends Component {
  urbit = new Urbit("http://localhost:8080", "lidlut-tabwed-pillex-ridrup");
  state = {
    entries: [],
    drafts: {},
    newDraft: {}
  };

  constructor() {
    super();
    this.urbit.ship = "zod"
  }

  componentDidMount() {
    this.getEntries();
    this.subscribe();
  }

  getEntries = async () => {
    const {entries: e} = this.state;
    const before = (e.length === 0) ? Date.now() : e[e.length - 1].id;
    const max = 2;
    const path = `/entries/before/${before}/${max}`;
    const result = await this.urbit.scry({
      app: "journal",
      path: path
    });
    this.handleUpdate(result);
  };

  subscribe = () => {
    this.urbit.subscribe({
      app: "journal",
      path: "/updates",
      event: this.handleUpdate,
    })
  };

  delete = (id) => {
    this.urbit.poke({
      app: "journal",
      mark: "journal-action",
      json: {"del": {"id": id}}
    })
  };

  submitEdit = (id, draft) => {
    if (draft !== null) {
      this.urbit.poke({
        app: "journal",
        mark: "journal-action",
        json: {"edit": {"id": id, "txt": draft}}
      })
    } else this.cancelEdit(id)
  };

  spot = (id) => {
    const {entries: e} = this.state;
    let low = 0
    let high = e.length;
    while (low < high) {
      let mid = (low + high) >>> 1;
      if (e[mid].id > id) low = mid + 1;
      else high = mid;
    }
    return low;
  }

  handleUpdate = (upd) => {
    const {entries, drafts} = this.state;
    if ("entries" in upd) {
      const newEntries = entries.concat(
        upd.entries.map(e => ({...e, edit: false}))
      );
      this.setState({entries: newEntries})
    } else if ("add" in upd) {
      const ind = this.spot(upd.add.id);
      if (upd.add.id > entries[entries.length - 1].id) {
        entries.splice(ind, 0, {...upd.add, edit: false})
      };
      this.setState({entries: entries});
    } else if ("edit" in upd) {
      const ind = entries.findIndex(e => e.id === upd.edit.id);
      if (ind !== -1) {
        entries[ind] = {...upd.edit, edit: false};
        delete drafts[upd.edit.id]
      };
      this.setState({entries: entries, drafts: drafts});
    } else if ("del" in upd) {
      const ind = entries.findIndex(e => e.id === upd.del.id);
      if (ind !== -1) {
        entries.splice(ind, 1);
        delete drafts[upd.del.id]
      };
      this.setState({entries: entries, drafts: drafts});
    };
  };

  cancelEdit = (id) => {
    const {entries, drafts} = this.state;
    const ind = entries.findIndex(e => e.id === id);
    if (ind !== -1) {
      entries[ind] = {...entries[ind], edit: false};
      delete drafts[id];
      this.setState({entries: entries, drafts: drafts})
    }
  };

  saveDraft = (id, text) => {
    const {drafts} = this.state;
    drafts[id] = text;
    this.setState({drafts: drafts})
  };

  setEdit = (id) => {
    const {entries} = this.state;
    const ind = entries.findIndex(e => e.id === id);
    if (ind !== -1) {
      entries[ind] = {...entries[ind], edit: true};
      this.setState({entries: entries})
    }
  };

  editBox = (id, txt, draft) => {
    return (
      <TextareaAutosize
        className="w-100 form-control"
        value={(draft === null) ? txt : draft}
        onChange={(e) => this.saveDraft(id, e.target.value)}
      />
    );
  };

  submitButton = (id, draft) => {
    return (
      <Button variant="outline-primary" onClick={()=>this.submitEdit(id, draft)}>
        Submit
      </Button>
    );
  };

  cancelButton = (id) => {
    return (
      <Button variant="outline-danger" onClick={()=>this.cancelEdit(id)}>
        Cancel
      </Button>
    )
  };

  editButton = (id) => {
    return (
      <Button variant="outline-primary" onClick={()=>this.setEdit(id)}>
        Edit
      </Button>
    )
  };

  deleteButton = (id) => {
    return (
      <Button variant="outline-danger" onClick={()=>this.delete(id)}>
        Delete
      </Button>
    )
  };

  printEntry = ({id, txt, edit}) => {
    const {drafts} = this.state;
    const draft = (id in drafts) ? drafts[id] : null;
    const reg = /(?:\r?\n[ \t]*){2,}(?!\s*$)/;
    let d = new Date(id);
    return (
      <Card key={id} style={{maxWidth: "40rem", width: "100%"}}>
        <Card.Header className="fs-4">{d.toLocaleString()}</Card.Header>
        <Card.Body>
          {(edit)
           ? this.editBox(id, txt, draft)
           : txt.split(reg).map((e, ind) => <p key={ind}>{e}</p>)
          }
        </Card.Body>
        <Card.Footer className="d-flex flex-wrap justify-content-between">
          {(edit) ? this.submitButton(id, draft) : this.editButton(id)}
          {(edit) ? this.cancelButton(id): this.deleteButton(id)}
        </Card.Footer>
      </Card>
    )
  };

  render() {
    return (
      <div className="m-3 d-flex align-items-center w-100">
        <Card style={{maxWidth: "40rem", width: "100%"}}>
          <Button onClick={()=>this.getEntries()}>Moar</Button>
          <Tabs defaultActiveKey="journal">
            <Tab eventKey="journal" title="Journal">
              <Stack gap={5} className="m-3 d-flex align-items-center">
                {this.state.entries.map(e => this.printEntry(e))}
              </Stack>
            </Tab>
            <Tab eventKey="new" title="New">
            </Tab>
            <Tab eventKey="search" title="Search">
            </Tab>
          </Tabs>
        </Card>
      </div>
    );
  }
}

export default App;
