import React, {Component} from 'react';
import Urbit from '@urbit/http-api';

class App extends Component {
  urbit = new Urbit("http://localhost:8080", "lidlut-tabwed-pillex-ridrup");
  state = {};

  constructor() {
    super();
    this.urbit.ship = "zod";
    this.state = this.getEntries(Date.now(), 5);
  };

  async getEntries(time, count) {
    const result = await this.urbit.scry({
      app: "journal",
      path: "/entries/before/${time}/${count}"
    });
    this.setState(result);
  };


  render() {
    return <pre>{JSON.stringify(this.state, null, 2)}</pre>;
  }
}

export default App;
