import React, {Component} from 'react';
import Urbit from '@urbit/http-api';
import 'bootstrap/dist/css/bootstrap.min.css';
import "react-widgets/styles.css";
import TextareaAutosize from 'react-textarea-autosize';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import Stack from 'react-bootstrap/Stack';
import Tab from 'react-bootstrap/Tab';
import Tabs from 'react-bootstrap/Tabs';
import CloseButton from 'react-bootstrap/CloseButton';
import DatePicker from "react-widgets/DatePicker";
import dayjs from 'dayjs';
import 'any-date-parser';

class App extends Component {
  urbit = new Urbit("http://localhost:8080");
  state = {
    entries: [],
    drafts: {},
    newDraft: {},
    results: [],
    searchStart: null,
    searchEnd: null,
    resultStart: null,
    resultEnd: null
  };

  constructor() {
    super();
    this.urbit.ship = "zod";
    this.state.searchStart = dayjs().startOf('day').toDate();
    this.state.searchEnd = dayjs().endOf('day').toDate()
  };

  componentDidMount() {
    this.getEntries();
    this.subscribe();
  };

  getEntries = async () => {
    const {entries: e} = this.state;
    const before = (e.length === 0) ? Date.now() : e[e.length - 1].id;
    const max = 2;
    const path = `/entries/before/${before}/${max}`;
    const result = await this.urbit.scry({
      app: "journal",
      path: path
    });
    this.handleUpdate(result);
  };

  subscribe = () => {
    this.urbit.subscribe({
      app: "journal",
      path: "/updates",
      event: this.handleUpdate,
    })
  };

  delete = (id) => {
    this.urbit.poke({
      app: "journal",
      mark: "journal-action",
      json: {"del": {"id": id}},
    })
  };

  submitEdit = (id, txt) => {
    if (txt !== null) {
      this.urbit.poke({
        app: "journal",
        mark: "journal-action",
        json: {"edit": {"id": id, "txt": txt}}
      })
    } else this.cancelEdit(id)
  };

  submitNew = (id, txt) => {
    this.urbit.poke({
      app: "journal",
      mark: "journal-action",
      json: {"add": {"id": id, "txt": txt}},
      onSuccess: ()=>this.setState({newDraft: {}})
    })
  };

  getSearch = async () => {
    const {searchStart: start, searchEnd: end} = this.state;
    if (start !== null || end !== null) {
      const path = `/entries/before/${start.getTime()}/${end.getTime()}`;
      const result = await this.urbit.scry({
        app: "journal",
        path: path
      });
      this.setState({
        results: result.entries,
        searchStart: null,
        searchEnd: null,
        resultStart: start,
        resultEnd: end
      })
    }
  };

  spot = (id) => {
    const {entries: e} = this.state;
    let low = 0
    let high = e.length;
    while (low < high) {
      let mid = (low + high) >>> 1;
      if (e[mid].id > id) low = mid + 1;
      else high = mid;
    }
    return low;
  }

  handleUpdate = (upd) => {
    const {entries, drafts} = this.state;
    if ("entries" in upd) {
      const newEntries = entries.concat(
        upd.entries.map(e => ({...e, edit: false}))
      );
      this.setState({entries: newEntries})
    } else if ("add" in upd) {
      const ind = this.spot(upd.add.id);
      if (upd.add.id > entries[entries.length - 1].id) {
        entries.splice(ind, 0, {...upd.add, edit: false})
      };
      this.setState({entries: entries});
    } else if ("edit" in upd) {
      const ind = entries.findIndex(e => e.id === upd.edit.id);
      if (ind !== -1) {
        entries[ind] = {...upd.edit, edit: false};
        delete drafts[upd.edit.id]
      };
      this.setState({entries: entries, drafts: drafts});
    } else if ("del" in upd) {
      const ind = entries.findIndex(e => e.id === upd.del.id);
      if (ind !== -1) {
        entries.splice(ind, 1);
        delete drafts[upd.del.id]
      };
      this.setState({entries: entries, drafts: drafts});
    };
  };

  cancelEdit = (id) => {
    const {entries, drafts} = this.state;
    const ind = entries.findIndex(e => e.id === id);
    if (ind !== -1) {
      entries[ind] = {...entries[ind], edit: false};
      delete drafts[id];
      this.setState({entries: entries, drafts: drafts})
    }
  };

  saveDraft = (id, text) => {
    const {drafts} = this.state;
    drafts[id] = text;
    this.setState({drafts: drafts})
  };

  setEdit = (id) => {
    const {entries} = this.state;
    const ind = entries.findIndex(e => e.id === id);
    if (ind !== -1) {
      entries[ind] = {...entries[ind], edit: true};
      this.setState({entries: entries})
    }
  };

  editBox = (id, txt, draft) => {
    return (
      <TextareaAutosize
        className="w-100 form-control"
        value={(draft === null) ? txt : draft}
        onChange={(e) => this.saveDraft(id, e.target.value)}
      />
    );
  };

  saveNew = (id, txt) => {
    (txt !== "")
      ? this.setState({newDraft: {id: id, txt: txt}})
      : this.setState({newDraft: {}})
  };

  newEntry = () => {
    const {newDraft: n} = this.state;
    const isNew = (Object.keys(n).length === 0);
    const now = Date.now();
    return (
      <div className="d-flex flex-column">
        <TextareaAutosize
          className="w-100 form-control"
          placeholder="Start a new journal entry here"
          value={(isNew) ? "" : n.txt}
          onChange={e => this.saveNew((isNew) ? now : n.id, e.target.value)}
        />
        {(!isNew) &&
          <Button
            className="w-100 mt-3"
            variant="outline-primary"
            onClick={()=>this.submitNew(n.id, n.txt)}
          >
            Submit
          </Button>
        }
      </div>
    )
  };

  printEntry = ({id, txt, edit}) => {
    const {drafts} = this.state;
    const draft = (id in drafts) ? drafts[id] : null;
    const reg = /(?:\r?\n[ \t]*){2,}(?!\s*$)/;
    let d = new Date(id);
    return (
      <Card key={id}>
        <Card.Header
          className="fs-4 d-flex align-items-center justify-content-between"
        >
          {d.toLocaleString()}
          <CloseButton
            className="fs-5"
            onClick={()=>(edit) ? this.cancelEdit(id) : this.delete(id)}
          />
        </Card.Header>
        <Card.Body onClick={()=>this.setEdit(id)}>
          {(edit)
           ? this.editBox(id, txt, draft)
           : txt.split(reg).map((e, ind) => <p key={ind}>{e}</p>)
          }
        </Card.Body>
        {(edit) &&
          <Button
            variant="outline-primary"
            className="mx-3 mb-3"
            onClick={()=>this.submitEdit(id, draft)}
          >
            Submit
          </Button>
        }
      </Card>
    )
  };

  setSearchStart = (when) => {
    if (when instanceof Date && !isNaN(when)) {
      const date = dayjs(when).startOf('day').toDate();
      this.setState({searchStart: date})
    } else this.setState({searchStart: null})
  };

  setSearchEnd = (when) => {
    if (when instanceof Date && !isNaN(when)) {
      const date = dayjs(when).endOf('day').toDate();
      this.setState({searchEnd: date})
    } else this.setState({searchEnd: null})
  };

  searcher = () => {
    const {searchStart: start, searchEnd: end} = this.state;
    return (
      <div className="d-flex justify-content-between">
        <div
          className="me-2 d-flex justify-content-start align-items-center flex-wrap"
        >
          <DatePicker
            className="w-25 my-1"
            value={start}
            valueFormat={{dateStyle: "medium"}}
            onChange={e => this.setSearchStart(e)}
            parse={str => Date.fromString(str)}
            style={{minWidth: "10em"}}
          />
          <div className="px-3 fs-4 my-1">to</div>
          <DatePicker
            className="w-25 my-1"
            value={end}
            valueFormat={{dateStyle: "medium"}}
            onChange={e => this.setSearchEnd(e)}
            parse={str => Date.fromString(str)}
            style={{minWidth: "10em"}}
          />
        </div>
        <Button
          className="w-20"
          variant={
            (start === null || end === null)
              ? "outline-secondary" : "outline-primary"
          }
          disabled={start === null || end === null}
          onClick={()=>this.getSearch()}
        >
          Search
        </Button>
      </div>
    )
  };

  resultEntry = ({id, txt}) => {
    const reg = /(?:\r?\n[ \t]*){2,}(?!\s*$)/;
    let d = new Date(id);
    return (
      <Card key={id}>
        <Card.Header
          className="fs-4 d-flex align-items-center justify-content-between"
        >
          {d.toLocaleString()}
        </Card.Header>
        <Card.Body onClick={()=>this.setEdit(id)}>
          {txt.split(reg).map((e, ind) => <p key={ind}>{e}</p>)}
        </Card.Body>
      </Card>
    )
  };

  render() {
    return (
      <div className="m-3 d-flex justify-content-center">
        <Card style={{maxWidth: "50rem", width: "100%"}}>
          <Button onClick={()=>this.getEntries()}>Moar</Button>
          <Tabs defaultActiveKey="journal" className="fs-2">
            <Tab eventKey="journal" title="Journal">
              <Stack gap={5} className="m-3 d-flex">
                {this.newEntry()}
                {this.state.entries.map(e => this.printEntry(e))}
              </Stack>
            </Tab>
            <Tab eventKey="search" title="Search">
              <Stack gap={5} className="m-3 d-flex">
                {this.searcher()}
                {this.state.results.map(e => this.printEntry(e))}
              </Stack>
            </Tab>
          </Tabs>
        </Card>
      </div>
    );
  }
}

export default App;
