import React, {Component} from 'react';
import Urbit from '@urbit/http-api';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'react-day-picker/lib/style.css';
import TextareaAutosize from 'react-textarea-autosize';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import Stack from 'react-bootstrap/Stack';
import Tab from 'react-bootstrap/Tab';
import Tabs from 'react-bootstrap/Tabs';
import ToastContainer from 'react-bootstrap/ToastContainer';
import Toast from 'react-bootstrap/Toast';
import Spinner from 'react-bootstrap/Spinner';
import CloseButton from 'react-bootstrap/CloseButton';
import Modal from 'react-bootstrap/Modal';
import DayPickerInput from 'react-day-picker/DayPickerInput';
import endOfDay from 'date-fns/endOfDay';
import startOfDay from 'date-fns/startOfDay';
import {BottomScrollListener} from 'react-bottom-scroll-listener';

window.urbit = new Urbit("http://localhost:8080");
window.urbit.ship = "zod";
//window.urbit.onError = async (err) => console.log("error");
//window.urbit.onRetry = () => console.log("retry");
//window.urbit.onOpen = () => console.log("open");

class App extends Component {
  state = {
    entries: [],
    drafts: {},
    newDraft: {},
    results: [],
    searchStart: null,
    searchEnd: null,
    resultStart: null,
    resultEnd: null,
    latestUpdate: null,
    rmModalShow: true,
    rmID: null
  };

  componentDidMount() {
    this.getEntries();
    this.subscribe();
  };

  stateChange = (obj) => this.setState(obj);

  getEntries = async () => {
    const {entries: e} = this.state;
    const before = (e.length === 0) ? Date.now() : e[e.length - 1].id;
    const max = 10;
    const path = `/entries/before/${before}/${max}`;
    const result = await window.urbit.scry({
      app: "journal",
      path: path
    });
    this.handleUpdate(result);
  };

  subscribe = () => {
    window.urbit.subscribe({
      app: "journal",
      path: "/updates",
      event: this.handleUpdate,
    })
  };

  delete = (id) => {
    window.urbit.poke({
      app: "journal",
      mark: "journal-action",
      json: {"del": {"id": id}},
    })
    this.stateChange({rmModalShow: false, rmID: null})
  };

  submitEdit = (id, txt) => {
    if (txt !== null) {
      window.urbit.poke({
        app: "journal",
        mark: "journal-action",
        json: {"edit": {"id": id, "txt": txt}}
      })
    } else this.cancelEdit(id)
  };

  submitNew = (id, txt) => {
    window.urbit.poke({
      app: "journal",
      mark: "journal-action",
      json: {"add": {"id": id, "txt": txt}},
      onSuccess: ()=>this.stateChange({newDraft: {}})
    })
  };

  getSearch = async () => {
    const {searchStart: ss, searchEnd: se} = this.state;
    if (ss !== null || se !== null) {
      let start = ss.getTime();
      let end = se.getTime();
      if (start < 0) start = 0;
      if (end < 0) end = 0;
      const path = `/entries/between/${start}/${end}`;
      const result = await window.urbit.scry({
        app: "journal",
        path: path
      });
      this.stateChange({
        searchStart: null,
        searchEnd: null,
        resultStart: ss,
        resultEnd: se,
        results: result.entries
      })
    }
  };

  spot = (id, data) => {
    let low = 0
    let high = data.length;
    while (low < high) {
      let mid = (low + high) >>> 1;
      if (data[mid].id > id) low = mid + 1;
      else high = mid;
    }
    return low;
  };

  shouldAddToResults = (id) => {
    const {resultStart: s, resultEnd: e} = this.state;
    return s !== null && e !== null && s.getTime() <= id <= e.getTime();
  };

  handleUpdate = (upd) => {
    console.log(upd);
    const {entries, drafts, results} = this.state;
    if ("entries" in upd) {
      this.stateChange({entries: entries.concat(upd.entries)})
    } else if ("add" in upd) {
      const eInd = this.spot(upd.add.id, entries);
      const rInd = this.spot(upd.add.id, results);
      const toE = (upd.add.id > entries[entries.length - 1].id);
      const toR = (this.shouldAddToResults(upd.add.id));
      toE && entries.splice(eInd, 0, upd.add);
      toR && results.splice(rInd, 0, upd.add);
      this.stateChange({
        ...(toE && {entries: entries}),
        ...(toR && {results: results}),
        latestUpdate: upd.time
      })
    } else if ("edit" in upd) {
      const eInd = entries.findIndex(e => e.id === upd.edit.id);
      const rInd = results.findIndex(e => e.id === upd.edit.id);
      if (eInd !== -1) entries[eInd] = upd.edit;
      if (rInd !== -1) results[rInd] = upd.edit;
      (eInd !== -1 || rInd !== -1) && delete drafts[upd.edit.id];
      this.stateChange({
        ...((eInd !== -1) && {entries: entries}),
        ...((rInd !== -1) && {results: results}),
        ...((eInd !== -1 || rInd !== -1) && {drafts: drafts}),
        latestUpdate: upd.time
      })
    } else if ("del" in upd) {
      const eInd = entries.findIndex(e => e.id === upd.del.id);
      const rInd = results.findIndex(e => e.id === upd.del.id);
      (eInd !== -1) && entries.splice(eInd, 1);
      (rInd !== -1) && results.splice(rInd, 1);
      (eInd !== -1 || rInd !== -1) && delete drafts[upd.del.id];
      this.stateChange({
        ...((eInd !== -1) && {entries: entries}),
        ...((rInd !== -1) && {results: results}),
        ...((eInd !== -1 || rInd !== -1) && {drafts: drafts}),
        latestUpdate: upd.time
      })
    };
  };

  cancelEdit = (id) => {
    const {drafts} = this.state;
    delete drafts[id];
    this.stateChange({drafts: drafts})
  };

  saveDraft = (id, text) => {
    const {drafts} = this.state;
    drafts[id] = text;
    this.stateChange({drafts: drafts})
  };

  setEdit = (id) => {
    const {drafts} = this.state;
    if (!(id in drafts)) {
      drafts[id] = null;
      this.stateChange({drafts: drafts})
    }
  };

  saveNew = (id, txt) => {
    (txt !== "")
      ? this.stateChange({newDraft: {id: id, txt: txt}})
      : this.stateChange({newDraft: {}})
  };

  newEntry = () => {
    const {newDraft: n} = this.state;
    const isNew = (Object.keys(n).length === 0);
    const now = Date.now();
    return (
      <div className="d-flex flex-column">
        <TextareaAutosize
          className="w-100 form-control"
          placeholder="Start a new journal entry here"
          value={(isNew) ? "" : n.txt}
          onChange={e => this.saveNew((isNew) ? now : n.id, e.target.value)}
        />
        {(!isNew) &&
          <Button
            className="w-100 mt-3"
            variant="outline-primary"
            onClick={()=>this.submitNew(n.id, n.txt)}
          >
            Submit
          </Button>
        }
      </div>
    )
  };

  closeRmModal = () => {
    this.stateChange({
      rmModalShow: false,
      rmID: null
    })
  };

  openRmModal = (id) => {
    this.stateChange({
      rmModalShow: true,
      rmID: id
    })
  };

  rmModal = () => {
    const {rmModalShow: show, rmID: id} = this.state;
    if (id !== null) {
      return (
        <Modal
          size="lg"
          centered
          show={show}
          onHide={()=>this.closeRmModal()}
        >
          <Modal.Header closeButton>
            <Modal.Title>Confirm Delete</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            Are you sure you want to delete this journal entry?
          </Modal.Body>
          <Modal.Footer>
            <Button
              variant="outline-danger"
              onClick={()=>this.delete(id)}
            >
              Delete
            </Button>
          </Modal.Footer>
        </Modal>
      )
    }
  };

  editBox = (id, txt, draft) => {
    return (
      <TextareaAutosize
        className="w-100 form-control"
        value={(draft === null) ? txt : draft}
        onChange={(e) => this.saveDraft(id, e.target.value)}
      />
    );
  };

  printEntry = ({id, txt}) => {
    const {drafts} = this.state;
    const edit = (id in drafts);
    const draft = (id in drafts) ? drafts[id] : null;
    const reg = /(?:\r?\n[ \t]*){2,}(?!\s*$)/;
    let d = new Date(id);
    return (
      <Card key={id}>
        <Card.Header
          className="fs-4 d-flex align-items-center justify-content-between"
        >
          {d.toLocaleString()}
          <CloseButton
            className="fs-6"
            onClick={() => (edit)
                     ? this.cancelEdit(id)
                     : this.openRmModal(id)
                    }
          />
        </Card.Header>
        <Card.Body onClick={()=>this.setEdit(id)}>
          {(edit)
           ? this.editBox(id, txt, draft)
           : txt.split(reg).map((e, ind) => <p key={ind}>{e}</p>)
          }
        </Card.Body>
        {(edit) &&
          <Button
            variant="outline-primary"
            className="mx-3 mb-3"
            onClick={()=>this.submitEdit(id, draft)}
          >
            Submit
          </Button>
        }
      </Card>
    )
  };

  setSearchStart = (when) => {
    if (when instanceof Date && !isNaN(when)) {
      const date = startOfDay(when);
      this.stateChange({searchStart: date})
    } else this.stateChange({searchStart: null})
  };

  setSearchEnd = (when) => {
    if (when instanceof Date && !isNaN(when)) {
      const date = endOfDay(when);
      this.stateChange({searchEnd: date})
    } else this.stateChange({searchEnd: null})
  };

  searcher = () => {
    const {
      searchStart: ss,
      searchEnd: se,
      resultStart: rs,
      resultEnd: re
    } = this.state;
    return (
      <Stack gap={5}>
        <div className="d-flex justify-content-between">
          <div
            className="me-2 d-flex justify-content-start align-items-center flex-wrap"
          >
            <DayPickerInput
              value={ss}
              placeholder="FROM  YYYY-M-D"
              onDayChange={day => this.setSearchStart(day)}
              style={{margin: "5px 5px 5px 0"}}
            />
            <DayPickerInput
              value={se}
              placeholder="TO  YYYY-M-D"
              onDayChange={day => this.setSearchEnd(day)}
              style={{margin: "5px 5px 5px 0"}}
            />
          </div>
          <Button
            className="w-20"
            variant={
              (ss === null || se === null)
                ? "outline-secondary" : "outline-primary"
            }
            disabled={ss === null || se === null}
            onClick={()=>this.getSearch()}
          >
            Search
          </Button>
        </div>
        { (rs !== null && re !== null) &&
            <div className="fs-4">
              Results for {rs.toLocaleDateString()} to {re.toLocaleDateString()}
            </div>
        }
      </Stack>
    )
  };

  status = () => {
    return (
      <ToastContainer
        style={{
          float: "right",
          position: "sticky",
          bottom: 0,
          width: "100%",
          zIndex: 999
        }}
      >
        <Toast bg="warning">
          <Toast.Body
            className="d-flex justify-content-center align-items-center"
          >
            <Spinner animation="border" size="sm" className="me-1"/>
            <strong>Reconnecting</strong>
          </Toast.Body>
        </Toast>
        <Toast bg="warning" className="w-100">
          <Toast.Body
            className="d-flex justify-content-center align-items-center"
          >
            <Spinner animation="border" size="sm" className="me-1"/>
            <strong>Reconnecting</strong>
          </Toast.Body>
        </Toast>
      </ToastContainer>
    )
  };

  render() {
    return (
      <React.Fragment>
        {this.rmModal()}
        <div className="m-3 d-flex justify-content-center">
          <Card style={{maxWidth: "50rem", width: "100%"}}>
            <Tabs defaultActiveKey="journal" className="fs-2">
              <Tab eventKey="journal" title="Journal">
                <BottomScrollListener onBottom={()=>this.getEntries()}>
                  {(scrollRef) =>
                    <Stack gap={5} className="m-3 d-flex">
                      {this.newEntry()}
                      {this.state.entries.map(e => this.printEntry(e))}
                    </Stack>
                  }
                </BottomScrollListener>
              </Tab>
              <Tab eventKey="search" title="Search">
                <Stack gap={5} className="m-3 d-flex">
                  {this.searcher()}
                  {this.state.results.map(e => this.printEntry(e))}
                </Stack>
              </Tab>
            </Tabs>
            {this.status()}
          </Card>
        </div>
      </React.Fragment>
    )
  }
}

export default App;
