import React, {Component} from 'react';
import Urbit from '@urbit/http-api';

class App extends Component {
  urbit = new Urbit("http://localhost:8080", "lidlut-tabwed-pillex-ridrup");
  state = {
    entries: [],
    latest: null,
    subID: null,
  };

  constructor() {
    super();
    this.urbit.ship = "zod";
  };

  componentDidMount() {
    this.getEntries();
    this.subscribe();
  }

  getEntries = async () => {
    const {entries: e} = this.state;
    const before = (e.length === 0) ? Date.now() : e[e.length - 1].id;
    const max = 1;
    const path = `/entries/before/${before}/${max}`;
    const result = await this.urbit.scry({
      app: "journal",
      path: path
    });
    this.handleUpdate(result);
  };

  subscribe = () => {
    this.urbit.subscribe({
      app: "journal",
      path: "/updates",
      event: this.handleUpdate
    })
  };

  spot(id) {
    const {entries: e} = this.state;
    let low = 0
    let high = e.length;
    while (low < high) {
      var mid = (low + high) >>> 1;
      if (e[mid].id > id) low = mid + 1;
      else high = mid;
    }
    return low;
  }

  handleUpdate = (upd) => {
    const e = this.state.entries;
    if ("entries" in upd) {
      this.setState({
        entries: e.concat(upd.entries)
      });
    } else if ("add" in upd) {
      const ind = this.spot(upd.add.id);
      const newE = e;
      if (upd.add.id > e[e.length - 1].id) newE.splice(ind, 0, upd.add);
      this.setState({
        entries: newE,
      });
    } else if ("edit" in upd) {
      const ind = e.findIndex(e => e.id === upd.edit.id);
      const newE = (ind !== -1) ? e[ind] = upd.edit : e;
      this.setState({
        entries: newE
      });
    } else if ("del" in upd) {
      const ind = e.findIndex(e => e.id === upd.del.id);
      const newE = e;
      if (ind !== -1) newE.splice(ind, 1);
      this.setState({
        entries: newE
      });
    };
  };

  render() {
    return (
      <React.Fragment>
        <button onClick={()=>this.getEntries()}>Moar</button>
        <pre>{JSON.stringify(this.state, null, 2)}</pre>
      </React.Fragment>
    );
  }
}

export default App;
